import React from 'react';
import { UserUpdateCardComponent } from './userupdatecard.component';
import { User } from '../../model/user';

interface IState {
  users : User[]
}

export class UserUpdateComponent extends React.Component<any, IState> {

  constructor(props: any) {
    super(props);
    this.state = {
      users: []
    };
  }

  // in here we should initialize http calls
  async componentDidMount() {
    const resp = await fetch('http://localhost:8081/users', { method: 'PATCH', credentials: 'include' });
       const body = await resp.json();
       this.setState({
         users: body
       })
  }

  render() {
    return (
      <div className="container">
        <div className="row">
          {this.state.users.map(user => (
            <UserUpdateCardComponent key={'User-' +user.user_id} user={user}/>
          ))}
        </div>
      </div>
    );
  }
}
