import React from 'react';
import { ReimbursementCardComponent } from './reimbursementcard.component';


interface IState {
  reimbursements : any;
  reimbursementId: number;
}

export class ReimbursementComponent extends React.Component<any, IState> {
    updatereimbursementId: any;

  constructor(props: any) {
    super(props);
    this.state = {
    reimbursements: undefined,
    reimbursementId: 0
    };
  }

  // in here we should initialize http calls
   getId = async (id:number) => {
      
    const resp = await fetch('http://localhost:8081/reimbursements/author/userId/' + id, { method: 'GET', credentials: 'include' });
       const body = await resp.json();
       this.setState({
         reimbursements: body
        
        })
        console.log(body);
  }
  
  updateReimbursementId = (event) => {
      this.setState({
          reimbursementId: event.target.value
      })

  }

  render() {
      const myReimbursements = this.state.reimbursements;
    return (
      <div className="container">
        <div className="row">
            <div className= "form-group">
            <input type = "number" className = "form-control" aria-describedby = "emailHelp" placeholder = "Enter reimbursement id" id ="user-input" value ={this.state.reimbursementId} onChange = {this.updateReimbursementId}/>
            <button type = "submit" className = "btn btn-primary" onClick={() => this.getId(+this.state.reimbursementId)}>Search</button>
            </div>
            <div className = "row"> 
             {myReimbursements && <>
                {
                    this.state.reimbursements.map(currReimb => (
                        <ReimbursementCardComponent key={'reimb-'+currReimb.reimbursementId} reimbursement={currReimb}/>
                    ))
                }

            </>}
            </div>
        </div>
      </div>
    );
  }
}
