import React from 'react';
import { number, string } from 'prop-types';
import { User } from '../../model/user';


interface IUserUpdateState {
    user: User;
    input: string;
    errorMessage: string;
}

export class UserUpdateComponent extends React.Component<any, any> {
    constructor(props) {
        super(props);
        this.state = {
            user: undefined,
            input: '',
            errorMessage: ''
        };
    }

    submit = async (event) => {
        event.preventDefault();
        console.log('attempting to login');
        let user = {
            user_id: number,
            user_name: string,
            password: string,
            firstname: string,
            lastname: string,
            email: string,
            user_role: undefined
        };

        try {
            const resp = await fetch('http://localhost:8081/users/', {
                method: 'PATCH',
                credentials: 'include',
                body: JSON.stringify(user),
                headers: {
                    'content-type': 'application/json'
                }
            })
            console.log(resp);

            if (resp.status === 401) {
                this.setState({
                    errorMessage: 'Invalid Credentials'
                });
            } else if (resp.status === 200) {
                // redirect to spaceships page
                // const user = await resp.json();
                this.props.history.push('/home');
            } else {
                this.setState({
                    errorMessage: 'Cannot Update User'
                });
            }
        } catch (err) {
            console.log(err);
        }
    }
    updateInput(e) {
        this.setState({
                input: e.target.value
            })
    }
    updateUserId = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                user_id: event.target.value
            }
        });
    }

    updateUsername = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                user_name: event.target.value
            }
        });
    }

    updatePassword = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                password: event.target.value
            }
        });
    }

    updateFirstName = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                firstname: event.target.value
            }
        });
    }

    updateLastName = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                lastname: event.target.value
            }
        });
    }

    updateEmail = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                email: event.target.value
            }
        });
    }

    updateRoleId = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                roleId: event.target.value
            }
        });
    }

    updateRole = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                user_role: event.target.value
            }
        });
    }
    render() {
        const { errorMessage } = this.state;
        return (

            <form className="form-signin" onSubmit={this.submit}>
                <h1 className="h3 mb-3 font-weight-normal">Please sign in</h1>
                <label htmlFor="inputUsername" className="sr-only">Username</label>
                <input type="text" id="inputUsername" name="username"
                    className="form-control" placeholder="Username"
                    required value={user} onChange={this.updateUsername} />

                <label htmlFor="firstname" className="sr-only">Username</label>
                    <input type="text" id="inputUsername" name="username"
                        className="form-control" placeholder="Username"
                        required value={firstname} onChange={this.updateUsername} />
                <label htmlFor="inputUsername" className="sr-only">Username</label>
                <input type="text" id="inputUsername" name="username"
                        className="form-control" placeholder="Username"
                        required value={user_name} onChange={this.updateUsername} />

                <button className="btn btn-lg btn-primary btn-block" type="submit">Sign in</button>
                <p id="login-error">{errorMessage}</p>
            </form>
        );
    }
}
