import React from 'react';
import { number, string } from 'prop-types';
import { User } from '../../model/user';
import { Role } from '../../model/Role';


interface IUserUpdateState {
    user: User;
    input: string;
    user_Id: number;
    user_name: string;
    password: string;
    firstname: string;
    lastname: string;
    email: string;
    user_role : Role;
    errorMessage: string;
}

export class UserUpdateComponent extends React.Component<any, any> {
    constructor(props) {
        super(props);
        this.state = {
            user: undefined,
            input: '',
            user_Id: 0,
             user_name: '',
             password: '',
            firstname: '',
            lastname: '',
            email: '',
            user_role : Role,
            errorMessage: string
        };
    }

    submit = async (event) => {
        event.preventDefault();
        console.log('attempting to login');
        let user = {
            user: undefined,
            input: number,
            user_id: number,
            user_name: string,
            password: string,
            firstname: string,
            lastname: string,
            email: string,
            user_role: undefined
        };

        try {
            const resp = await fetch('http://localhost:8081/users/', {
                method: 'PATCH',
                credentials: 'include',
                body: JSON.stringify(user),
                headers: {
                    'content-type': 'application/json'
                }
            })
            console.log(resp);

            if (resp.status === 401) {
                this.setState({
                    errorMessage: 'Invalid Credentials'
                });
            } else if (resp.status === 200) {
                // redirect to spaceships page
                // const user = await resp.json();
                this.props.history.push('/home');
            } else {
                this.setState({
                    errorMessage: 'Cannot Update User'
                });
            }
        } catch (err) {
            console.log(err);
        }
    }
    updateInput(e) {
        this.setState({
                input: e.target.value
            })
    }
    updateUserId = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                user_id: event.target.value
            }
        });
    }

    updateUsername = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                user_name: event.target.value
            }
        });
    }

    updatePassword = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                password: event.target.value
            }
        });
    }

    updateFirstName = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                firstname: event.target.value
            }
        });
    }

    updateLastName = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                lastname: event.target.value
            }
        });
    }

    updateEmail = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                email: event.target.value
            }
        });
    }

    updateRole = (event) => {
        this.setState({
            user: {
                ...this.state.user,
                user_role: event.target.value
            }
        });
    }
    render() {
                    return (
                <div className="container">
                  <div className="row">
                  <div className="card col-md-4 col-sm-6 col-xs-12">
                  <input type="text" value={this.state.input} onChange={(e) => this.updateInput(e)}></input><br></br>
           
                  <div className="card-body">
                    <h5 className="card-title">{this.state.user.firstname + ' ' + this.state.user.lastname}</h5>
                  </div>
                  <ul className="list-group list-group-flush">
                    <li className="list-group-item">ID: {this.state.user.user_id}</li>
                    <li className="list-group-item">Username: {this.state.user.user_name}</li>
                    <li className="list-group-item">Password: {this.state.user.password}</li>
                    <li className="list-group-item">First Name: {this.state.user.firstname}</li>
                    <li className="list-group-item">Last Name: {this.state.user.lastname}</li>
                    <li className="list-group-item">email: {this.state.user.email}</li>
                    <li className="list-group-item">Role: {this.state.user.user_role}</li>
                  </ul>
                  </div>
                  </div>
                  <form className="form-signin" onSubmit={this.submit}>
                  <h1 className="h3 mb-3 font-weight-normal">Update Profile</h1>
           
                  <label>User ID</label>
                  <input type="text" id="inputUserId" name="userId"
                    className="form-control" placeholder="UserId"
                    required value={this.state.user.user_id} onChange={this.updateUserId} />
           
                  <label>Username</label>
                  <input type="text" id="inputUsername" name="username"
                    className="form-control" placeholder="Username"
                    required value={this.state.user.user_name} onChange={this.updateUsername} />
           
                  <label>Password</label>
                  <input type="password" id="inputPassword" name="password"
                    className="form-control" placeholder="Password"
                    required value={this.state.user.password} onChange={this.updatePassword} />
           
                  <label>First Name</label>
                  <input type="text" id="inputFirstName" name="firstName"
                    className="form-control" placeholder="First Name"
                    required value={this.state.user.firstname} onChange={this.updateFirstName} />
           
                  <label>Last Name</label>
                  <input type="text" id="inputLastName" name="lastName"
                    className="form-control" placeholder="Last Name"
                    required value={this.state.user.lastname} onChange={this.updateLastName} />
           
                  <label>email</label>
                  <input type="text" id="inputEmail" name="email"
                    className="form-control" placeholder="email"
                    required value={this.state.user.email} onChange={this.updateEmail} />
           
                  <label>Role</label>
                  <input type="text" id="inputRole" name="role"
                    className="form-control" placeholder="Role"
                    required value={this.state.user.user_role} onChange={this.updateRole} />
           
                  <button className="btn btn-lg btn-primary btn-block" type="submit">Update</button>
                  <p id="login-error">{this.state.errorMessage}</p>
                </form>
                </div>
        );
    }
}
